export const loadNotoFont = async () => {
  const notoFonts = [
    'https://fonts.gstatic.com/s/notocoloremoji/v25/Yq6P-KqIXTD0t4D9z1ESnKM3-HpFabsE4tq3luCC7p-aXxcn.0.woff2',
    'https://fonts.gstatic.com/s/notocoloremoji/v25/Yq6P-KqIXTD0t4D9z1ESnKM3-HpFabsE4tq3luCC7p-aXxcn.1.woff2',
    'https://fonts.gstatic.com/s/notocoloremoji/v25/Yq6P-KqIXTD0t4D9z1ESnKM3-HpFabsE4tq3luCC7p-aXxcn.2.woff2',
    'https://fonts.gstatic.com/s/notocoloremoji/v25/Yq6P-KqIXTD0t4D9z1ESnKM3-HpFabsE4tq3luCC7p-aXxcn.3.woff2',
    'https://fonts.gstatic.com/s/notocoloremoji/v25/Yq6P-KqIXTD0t4D9z1ESnKM3-HpFabsE4tq3luCC7p-aXxcn.4.woff2',
    'https://fonts.gstatic.com/s/notocoloremoji/v25/Yq6P-KqIXTD0t4D9z1ESnKM3-HpFabsE4tq3luCC7p-aXxcn.5.woff2',
    'https://fonts.gstatic.com/s/notocoloremoji/v25/Yq6P-KqIXTD0t4D9z1ESnKM3-HpFabsE4tq3luCC7p-aXxcn.6.woff2',
    'https://fonts.gstatic.com/s/notocoloremoji/v25/Yq6P-KqIXTD0t4D9z1ESnKM3-HpFabsE4tq3luCC7p-aXxcn.7.woff2',
    'https://fonts.gstatic.com/s/notocoloremoji/v25/Yq6P-KqIXTD0t4D9z1ESnKM3-HpFabsE4tq3luCC7p-aXxcn.8.woff2',
    'https://fonts.gstatic.com/s/notocoloremoji/v25/Yq6P-KqIXTD0t4D9z1ESnKM3-HpFabsE4tq3luCC7p-aXxcn.9.woff2',
  ];

  const fontName = 'Noto Color Emoji';
  const fontFiles = notoFonts.map((url) => new FontFace(fontName, `url(${url})`));

  await Promise.allSettled(
    fontFiles.map((fontFile) => {
      document.fonts.add(fontFile);
      return fontFile.load();
    }),
  );
};

export const bg = (ctx: CanvasRenderingContext2D, color?: string) => {
  const { width, height } = getCanvasDimensions(ctx);

  const currentColor = ctx.fillStyle;
  ctx.fillStyle = color || 'aliceblue';
  ctx.fillRect(0, 0, width, height);
  ctx.fillStyle = currentColor;
};

export const reset = (ctx: CanvasRenderingContext2D) => {
  ctx.reset();
};

export const getCanvasDimensions = (ctx: CanvasRenderingContext2D) => {
  const canvas = ctx.canvas;
  const width = canvas.width;
  const height = canvas.height;

  return { width, height };
};

export const adjustFontSize = (
  text: string,
  maxWidth: number | null,
  maxHeight: number | null,
  font: string = 'sans-serif',
  fontSize: number = 24,
) => {
  let left = 0;
  let right = fontSize;
  let guard = 0;

  const epsilon = 0.01;

  const initialFitsWidth = !maxWidth || measureText(text, `${right}px ${font}`).width <= maxWidth;
  const initialFitsHeight = !maxHeight || measureText(text, `${right}px ${font}`).height <= maxHeight;

  if (initialFitsWidth && initialFitsHeight) {
    return right;
  }

  while (Math.abs(left - right) > epsilon) {
    const mid = (left + right) / 2;
    const fitsWidth = !maxWidth || measureText(text, `${mid}px ${font}`).width <= maxWidth;
    const fitsHeight = !maxHeight || measureText(text, `${mid}px ${font}`).height <= maxHeight;

    if (fitsWidth && fitsHeight) {
      left = mid;
    } else {
      right = mid;
    }

    guard++;

    if (guard > 10000) {
      break;
    }
  }

  return left;
};

export const measureText = (text: string, font?: string) => {
  const canvas = document.createElement('canvas');
  const ctx = canvas.getContext('2d');

  if (!ctx) return { width: 0, height: 0 };
  ctx.font = font || '24px sans-serif';

  const measurement = ctx.measureText(text);

  return {
    width: measurement.width,
    height: measurement.actualBoundingBoxAscent + measurement.actualBoundingBoxDescent,
    actualBoundingBoxAscent: measurement.actualBoundingBoxAscent,
    actualBoundingBoxDescent: measurement.actualBoundingBoxDescent,
  };
};

export const getShapeDimensions = (svgPath: string) => {
  const svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
  const path = document.createElementNS('http://www.w3.org/2000/svg', 'path');

  path.setAttribute('d', svgPath);
  svg.appendChild(path);
  svg.style.position = 'absolute';
  svg.style.top = '-1000px';
  svg.style.left = '-1000px';
  svg.style.visibility = 'hidden';

  document.body.appendChild(svg);

  const bbox = path.getBBox();

  document.body.removeChild(svg);

  return { width: bbox.width + bbox.x, height: bbox.height + bbox.y };
};

export const fitSource = (sourceWidth: number, sourceHeight: number, boxWidth: number, boxHeight: number) => {
  const sourceAspectRatio = sourceWidth / sourceHeight;
  const boxAspectRatio = boxWidth / boxHeight;

  let newBoxWidth: number;
  let newBoxHeight: number;

  if (sourceAspectRatio < boxAspectRatio) {
    newBoxWidth = sourceWidth;
    newBoxHeight = sourceWidth / boxAspectRatio;
  } else {
    newBoxHeight = sourceHeight;
    newBoxWidth = sourceHeight * boxAspectRatio;
  }

  const x = (sourceWidth - newBoxWidth) / 2;
  const y = (sourceHeight - newBoxHeight) / 2;

  return {
    width: newBoxWidth,
    height: newBoxHeight,
    x,
    y,
  };
};
